import axios from 'axios'

const initialState = {
  photos: {}
}

export const types = {
  GETPHOTOS: 'PHOTOS/GETPHOTOS',
  GETPHOTOSBYDATE: 'PHOTOS/GETPHOTOSBYDATE'
}

export const actions = {
  getPhotos: () => {
    let photos
    axios
      .get('http://127.0.0.1/api/getPhotos')
      .then(res => {
        photos = res
        console.log(res)
      })
      .catch(err => {
        console.log(err)
      })
    return { type: types.GETPHOTOS, photos: photos }
  },
  getPhotosByDates: dates => {
    let photos
    axios
      .post('http://127.0.0.1/api/getPhotosByDate', { dates })
      .then(res => {
        console.log(res)
        photos = res
      })
      .catch(err => {
        console.log(err)
      })
    return {
      type: types.GETPHOTOSBYDATE,
      photos: photos
    }
  }
}

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case types.GETPHOTOS:
      return {
        ...state,
        photos: action.photos
      }
    case types.GETPHOTOSBYDATE:
      return {
        ...state,
        photos: action.photos
      }
    default:
      return state
  }
}

export default reducer
