import React, { Component } from 'react'
import { connect } from 'react-redux'
import { bindActionCreators } from 'redux'
import { Redirect, withRouter, Link } from 'react-router-dom'
import { DatePicker, Button } from 'antd'
import { actions as photosActions, getPhotos } from '../../redux/photos'
import { actions as authActions, getUsername } from '../../redux/auth'
import './Photos.css'
import { errImg } from '../../images/index'

class Photos extends Component {
  onChange = dates => {
    console.log(dates)
    this.props.getPhotosByDates(dates)
  }
  logout = () => {
    this.props.logout()
  }

  componentDidMount() {
    this.props.getPhotos()
  }

  render() {
    const { RangePicker } = DatePicker
    const username = this.props.username
    const {
      photos: { data = [] }
    } = this.props

    return username ? (
      <div className="photos-container">
        <div className="header-container">
          <div className="header">
            <h1>zhou_pp的照片墙</h1>
            <div className="user">
              <span>用户：</span>
              <span>{username}</span>
              {username === '呆呆' ? (
                <Link to="/administer">
                  <Button type="normal" className="manage">
                    管理
                  </Button>
                </Link>
              ) : (
                ''
              )}
              <Button
                type="normal"
                className="logout"
                onClick={() => {
                  this.logout()
                }}
              >
                退出登录
              </Button>
            </div>
            <div className="range">
              <span>选择照片时段：</span>
              <RangePicker
                className="range-picker"
                size="medium"
                onChange={dates => this.onChange(dates)}
              />
            </div>
          </div>
        </div>
        <div className="photos-body">
          <div className="photo-contents">
            {!(data && data.length) ? (
              <p>抱歉，没有当前时段的照片~</p>
            ) : (
              data.map((item, index, arr) => {
                let marginRight = (index + 1) % 4 === 0 ? 0 : 10
                return (
                  <img
                    style={{ marginRight: `${marginRight}px` }}
                    key={item['picture_Id']}
                    src={item['picture_content']}
                    title={item['picture_description']}
                    onError={e => {
                      e.target.onError = null
                      e.target.src = errImg
                    }}
                  />
                )
              })
            )}
          </div>
        </div>
      </div>
    ) : (
      <Redirect to="/" />
    )
  }
}

const mapStateToProps = state => {
  return {
    username: getUsername(state.auth),
    photos: getPhotos(state.photos)
  }
}

const mapDispatchToProps = dispatch => {
  return {
    ...bindActionCreators(photosActions, dispatch),
    ...bindActionCreators(authActions, dispatch)
  }
}

export default withRouter(connect(mapStateToProps, mapDispatchToProps)(Photos))
